SELECT C.NOMBRE, NOMBRE_MENU AS PRODUCTO, NVL(SUM(C.PRECIO)*SUM(C.CANTIDADASIGNADA),0) AS TOTALASIGNADO, NVL(SUM(CANTIDADASIGNADA),0) AS CANTIDADASIGNADA,   NVL(SUM(C.PRECIO)*SUM(C.CANTIDADNOASIGNADA),0) AS TOTALNOASIGNADO, NVL(SUM(CANTIDADNOASIGNADA),0) AS CANTIDADNOASIGNADA  
FROM (SELECT NOMBRE_MENU, B.NOMBRE_RESTAURANTE AS NOMBRE, (CASE WHEN C.IDUSUARIO IS NULL THEN 0 ELSE B.CANTIDAD END) AS CANTIDADASIGNADA,  (CASE WHEN C.IDUSUARIO IS NOT NULL THEN 0 ELSE B.CANTIDAD END) AS CANTIDADNOASIGNADA, A.PRECIO  FROM (MENU A LEFT OUTER JOIN PEDIDO_MENU B ON A.NOMBRE=B.NOMBRE_MENU) LEFT OUTER JOIN CUENTA C ON B.NUMERO_CUENTA=C.NUMEROCUENTA   WHERE C.PAGADA='1'  ) C ,RESTAURANTE D 
WHERE D.NOMBRE LIKE C.NOMBRE group by C.NOMBRE, NOMBRE_MENU ORDER BY C.NOMBRE, NOMBRE_MENU;
