WHERE PAGADA='1' AND D.NOMBRE LIKE B.NOMBRE_RESTAURANTE group by B.NOMBRE_RESTAURANTE, NOMBRE_MENU ORDER BY NOMBRE_RESTAURANTE, NOMBRE_MENU;
